CREATE VIEW "public"."avg_rating_view" AS (select "toilet_id", ROUND(AVG("rating_cleanliness"::numeric), 2) as "avg_cleanliness", ROUND(AVG("rating_accessibility"::numeric), 2) as "avg_accessibility", ROUND(AVG("rating_location"::numeric), 2) as "avg_location", ROUND(AVG(("rating_cleanliness" + "rating_accessibility" + "rating_location")::numeric / 3), 2) as "avg_rating_overall", COUNT(*) as "total_ratings" from "ToiletRating" group by "toilet_id");